mapper.select:
SELECT mr.name, temp.meeting FROM tb_meeting_room mr LEFT JOIN( SELECT place, CONCAT( "[", GROUP_CONCAT( JSON_OBJECT('start',DATE_FORMAT(start, '%H:%i') , 'end',DATE_FORMAT(end , '%H:%i'), 'status', `status`, 'time', ROUND(timestampdiff(MINUTE,CONCAT(`date`, " ", start),CONCAT(`date`," ",end))/30) ) ), "]")AS meeting FROM tb_meeting WHERE type = 2 AND `status`!= 2 AND date=#{date}
AND JSON_CONTAINS ( members,CAST(#{userId} As CHAR))
GROUP BY place) temp ON mr.name=temp.place WHERE mr.`status`=1 LIMIT #{start},#{length}

mapper.update:
UPDATE tb_meeting
SET instance_id=#{instanceId}
WHERE uuid=#{uuid}

mapper.select:
SELECT id, title, DATE_FORMAT(`date`,"%m/%d")AS `date`, DATE_FORMAT(`start` ,"%H:%i")AS `start`, ROUND(timestampdiff(MINUTE,CONCAT(`date`," ",`start`),CONCAT(`date`," ",`end`))/30) as time ,
uuid, instance_id AS instanceId, IF(creator_id = #{userId},"true","false")AS isCreator, `status` FROM tb_meeting WHERE type = 2 AND place = #{place} AND `status` != 2 AND date BETWEEN #{startDate} AND #{endDate}
AND JSON_CONTAINS(members,CAST(#{userId} AS CHAR))

mapper.delete:
delete
from tb_meeting
where creator_id=#{creatorId}
AND id=#{id}

mapper.select:
SELECT id, IF(creator_id=#{userId},"true","false") AS isCreator, title,(select name from tb_user where id = creator_id)AS creatorName,date, CONCAT(DATE_FORMAT(`start`,'%H:%i'),"~",DATE_FORMAT(`end`,'%H:%i'))as time ,m.desc,m.status,(LENGTH(members)-length(replace(members,',',''))+1)as count from tb_meeting m WHERE place = "1"
AND date=#{date}
AND JSON_CONTAINS ( members,CAST(#{userId} As CHAR))
LIMIT #{start},#{length}
